/* Function  structure for function  `substring_regexp` */

/*!50003 DROP FUNCTION IF EXISTS `substring_regexp` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` FUNCTION `substring_regexp`(cadena text, re text, sustitucion text) RETURNS text CHARSET latin1 COLLATE latin1_swedish_ci
    DETERMINISTIC
BEGIN
declare a, b, tam int;
declare subcadena, retorno text;
set tam = 0;
set retorno = '';
set a = 1;
inicio: WHILE a <= length(cadena) DO
	set b = a;
	final: WHILE b <= length(cadena) DO
		set subcadena = substring(cadena, a, b-a+1);
		if subcadena regexp concat('^', re, '$') and length(subcadena) > tam then
			set tam = length(subcadena);
			if sustitucion is null then
				set retorno = subcadena;
			else
				set retorno = replace(cadena, subcadena, sustitucion);
			end if;
		end if;
		set b = b+1;
	END WHILE final;
	set a = a+1;
END WHILE inicio;
return retorno;
END */$$
DELIMITER ;

