DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `lab_actualizar_estado_resultados`(IN idResultado BIGINT)
BEGIN        
        UPDATE l_detallerecepcion, (
        WITH maestra AS (
                SELECT ld.Id_Recepcion idRecepcion, ld.Id_Procedim idProcedimiento , COUNT(lp.Id_Protocolo) cantidad 
                FROM l_detallerecepcion ld 
                INNER JOIN l_protocoloxprocedimiento lp ON (lp.Id_Procedim=ld.Id_Procedim) AND (lp.Estado=0)
                WHERE ld.Id_Recepcion =idResultado AND lp.esObligatorio=1
                GROUP BY ld.Id_Recepcion , ld.Id_Procedim 
        ),
        resultado AS (
                SELECT  ld.Id_Procedimiento idProcedimiento , COUNT(lp.Id_Protocolo) numeroResultado
                FROM l_resultados lr 
                INNER JOIN l_detalleresultado ld ON (lr.Id=ld.Id_Resultado)
                INNER JOIN l_protocoloxprocedimiento lp ON (lp.Id=ld.Id_ProtocolProcedim)
                INNER JOIN l_detallerecepcion ld2 ON (ld2.Id_Recepcion=lr.Id_Recepcion) AND (ld2.Id_Procedim=ld.Id_Procedimiento )
                WHERE lr.Id_Recepcion =idResultado  AND lp.esObligatorio =1 AND lr.Estado =0 AND ld2.ValidadoLab =1
                GROUP BY lr.Id_Recepcion, ld.Id_Procedimiento 
        )
        SELECT m.idRecepcion,  m.idProcedimiento, IF(IFNULL(r.numeroResultado, 0)=0,'PENDIENTE', IF(m.cantidad=IFNULL(r.numeroResultado, 0), 'COMPLETADO', 'PARCIALMENTE')) estado
        FROM maestra m
        LEFT JOIN resultado r ON (m.idProcedimiento=r.idProcedimiento)
        ) d SET l_detallerecepcion.validacionEstado =d.estado 
        WHERE l_detallerecepcion.Id_Recepcion =d.idRecepcion 
        AND l_detallerecepcion.Id_Procedim =d.idProcedimiento;
END */$$
DELIMITER ;

