/* Procedure structure for procedure `so_actualizacion_datos` */

/*!50003 DROP PROCEDURE IF EXISTS  `so_actualizacion_datos` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `so_actualizacion_datos`(IN mdatos INT, IN xid INT)
BEGIN
	  DECLARE var INT;
	  SET var=mdatos;
	  CASE var
	  WHEN 1 THEN UPDATE  `so_manporwer_detalle`, `v_so_atencion_detalle` 
		      SET so_manporwer_detalle.`FUPeriodico`=v_so_atencion_detalle.Fecha_Atencion
		      WHERE so_manporwer_detalle.`Id_mampower`=xid  AND so_manporwer_detalle.`Id_Persona`=v_so_atencion_detalle.Id_Usuario;
	  WHEN 2 THEN UPDATE  `g_persona` SET g_persona.`ActivoSo`=0  WHERE g_persona.`ActivoSo`=1;
	  WHEN 3 THEN UPDATE `so_manporwer_detalle`, `g_persona` 
			SET so_manporwer_detalle.`Id_Persona`=g_persona.`Id` , g_persona.`ActivoSo`=1, so_manporwer_detalle.`FUPeriodico`= g_persona.`FEPeriodico`
			WHERE g_persona.`NoDocumento`=so_manporwer_detalle.`Cedula` ;
	  WHEN 4 THEN UPDATE g_persona, `v_so_atencion_detalle` 
		      SET g_persona.`FEPeriodico`=v_so_atencion_detalle.Fecha_Atencion
		      WHERE  g_persona.`Id`=v_so_atencion_detalle.Id_Usuario;
	  WHEN 5 THEN UPDATE `so_manporwer_detalle`, `g_persona` 
			SET so_manporwer_detalle.`Id_Persona`=g_persona.`Id`
			WHERE g_persona.`NoDocumento`=so_manporwer_detalle.`Cedula`  and so_manporwer_detalle.`Id_Persona`=0;  
		      
	  WHEN 6 THEN UPDATE `so_manporwer_detalle`, `g_usuario` ,so_tipo_segmento
			SET g_usuario.`Id_Segmento`= so_tipo_segmento.`Id`
			WHERE so_manporwer_detalle.`SEG`=so_tipo_segmento.`Nbre` 
			AND so_manporwer_detalle.`Id_Persona`=g_usuario.`Id_persona`
			AND so_manporwer_detalle.`Id_mampower`=xid;
	  END CASE;	
		
	END */$$
DELIMITER ;

